import {LitElement, css, html} from "lit";
import {kbFooterColumns} from "./kb-footer-columns";
import {property} from "lit/decorators.js";
import {BSContainer, BSRow, BSColSM6, BSColLG3, BSH3, KBColors} from "@kb-dk/kb-style/index.js"

export class KbFooter extends LitElement {

    @property() language = 'da';
    @property() jsonUuId = '';
    @property() bgcolor = '';
    render() {
        return html`
            <footer class="global-footer ${this.bgcolor}">
                <nav class="container">
                    <div class="row">
                        ${kbFooterColumns(this.language, this.jsonUuId)}
                    </div>
                </nav>
            </footer>
        `;
    }

    static styles = [
        BSContainer,
        BSRow,
        BSColSM6,
        BSColLG3,
        BSH3,
        KBColors,
        css`
          :host {
            letter-spacing: -.0125rem;
            font-size: 1rem;
            line-height: 1.5;
          }
          
          footer.global-footer .container {
            padding-left: 24px;
            padding-right: 24px;
            margin-left: 0;
            margin-right: 0;
          }

          footer.global-footer.skyblue {
              background-color: var(--kb-color-sky-blue-50);
              color: var(--kb-color-dark-blue-100);
          }
          footer.global-footer {
            background-color: var(--kb-color-primary);
            color: var(--kb-color-white-100);
            padding: 36px 0 40px;
            font-family: noway, sans-serif;
            font-weight: 400;
          }

          footer.global-footer .col-sm-6 {
            padding-bottom: 48px;
            position: relative;
          }

          @media (min-width: 992px) {
            footer.global-footer {
              padding: 36px 0 40px;
              display: block;
            }

            footer.global-footer .container {
              padding-left: 12px;
              padding-right: 12px;
              margin-left: auto;
              margin-right: auto;
            }
             .skyblue .rdl-logo.rdl-logo-inverted {
                 background-image: url("data:image/svg+xml;base64,");
             }
            .rdl-logo.rdl-logo-inverted {
              background-image: url("data:image/svg+xml;base64,");
              width: 174px;
            }
          }


          footer div ul {
            list-style-type: none;
            padding-inline-start: 0;
            margin-bottom: 1rem;
          }

          footer h2.h3 {
            font-weight: 700;
            line-height: 1.5rem;
            margin-bottom: 12px;
            margin-top: 0;
            font-size: 1.25rem;
          }

          footer a {
            text-decoration: none;
            color: var(--kb-color-white-100);
          }
          footer.skyblue a{
              color: var(--kb-color-dark-blue-100);
          }
          .some-icons {
            position: absolute;
            bottom: 0;
            left: 50%;
            transform: translateX(-50%);
            margin-top: 16px;
          }

          .some-icons a {
            background-color: var(--kb-color-white-100);
            border-radius: 50%;
            width: 24px;
            height: 24px;
            display: inline-flex;
            justify-content: center;
            align-items: center;
            margin-right: 12px;
          }

          footer.global-footer .some-icons a {
            align-items: center;
            background-color: var(--kb-color-white-100);
            border-radius: 50%;
            display: inline-flex;
            height: 24px;
            justify-content: center;
            margin-right: 12px;
            width: 24px;
          }

          .some-icons a:last-child {
            margin-right: 0;
          }

          .some-icons a i {
            font-size: 0.875rem;
            color: var(--kb-color-primary);
          }

          .some-icons a:hover, .some-icons a:focus {
            text-decoration: none;
          }

          @media (min-width: 576px) {
            footer.global-footer .some-icons {
              left: inherit;
              position: inherit;
              transform: none;
            }
          }

          footer.global-footer li, 
          footer.global-footer p{
            margin-bottom: 12px;
          }

          footer.global-footer .rdl-logo {
            height: 48px;
            margin-bottom: 16px;
          }

          .rdl-logo {
            background-position: 0;
            background-repeat: no-repeat;
            background-size: contain;
            display: inline-block;
            height: 32px;
            width: 138px;
            max-width: initial;
            padding-bottom: 0;
          }

          .rdl-logo-inverted {
            background-image: url("data:image/svg+xml;base64,");
          }
           .skyblue .rdl-logo-inverted {
             background-image:  url("data:image/svg+xml; base64,");
           }
          .sr-only {
            clip: rect(0, 0, 0, 0);
            border: 0;
            height: 1px;
            margin: -1px;
            overflow: hidden;
            padding: 0;
            position: absolute;
            white-space: nowrap;
            width: 1px;
          }
          footer.global-footer a:hover, footer.global-footer a:focus {
                text-decoration: underline;
           }
        `
    ];
}


if (!customElements.get( 'kb-footer' )){
    customElements.define('kb-footer', KbFooter);
}